import React, { useState } from "react";

import ExpenseForm from "./ExpenseForm";
import "./NewExpense.css";

const NewExpense = (props) => {
  const [expandForm, setExpandForm] = useState(false);

  const expandChangeHandler = (expand) => {
    setExpandForm(expand);
  };

  const saveExpanseDataHandler = (enteredExpanseData) => {
    const expanseData = {
      ...enteredExpanseData,
      id: Math.random().toString(),
    };
    props.onAddExpense(expanseData);
  };

  let expenseFormCondition = <button onClick={() => setExpandForm(true)}>Add New Expense</button>;
  if (expandForm) {
    <ExpenseForm
      onExpandChange={expandChangeHandler}
      onSaveExpenseData={saveExpanseDataHandler}
    />;
  }

  return <div className="new-expense">{expenseFormCondition}</div>;
};

export default NewExpense;
