  const startEditingHandler = () => {
    setExpandForm(true);
  };

  const saveExpanseDataHandler = (enteredExpanseData) => {
    const expanseData = {
      ...enteredExpanseData,
      id: Math.random().toString(),
    };
    props.onAddExpense(expanseData);
  };

  let expenseFormCondition = (
    <button onClick={() => setExpandForm(true)}>Add New Expense</button>
  );
